package com.syntax.class07;

public class DoLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int num=1;
         
         while (num<=5) {
        	 System.out.println("hello");  //5times
        	 num++;
         }
		
		
		int num1=1;
		do {
			System.out.println("Hello");
			num1++;
			
			}while (num1<=3);
		

		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
