package com.syntax.class07;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1; i<=5; i++) {
		System.out.println("Hello");
		
		
		
	}	
		System.out.println("I need to print numbers from 50 to 1");
		
		for (int i=50; i>=1; i--) {
			System.out.print(i+" ");
		}
		

	}

}
